import React from 'react';
import Dice from './Dice';
import '../styles/Game.scss';

const Game = () => {

    const [roll, setRoll] = useState(0);

    const rollDice = () => {
        setRoll(roll + 1);
    };
    
    return (
        <div className="game">
            <h1>React Dice Game</h1>
            <Dice key={roll} />
            <button className="roll-button" onClick={rollDice}>Tirar los dados</button>
        </div>
    );
};

export default Game;
