import React, { useState } from 'react';
import '../styles/Menu.scss';
import GameRules from './components/GameRules';
const Menu = () => {

    const [isRulesVisible, setIsRulesVisible] = useState(false);

    const showRules = () => {
        setIsRulesVisible(!isRulesVisible);
    }

    const reloadPage = () => {
        window.location.reload();
    }

    return (
        <div className='menu'>
            <ul className='dropdown-menu'>
                <li className='menu-links' onClick={showRules}>{isRulesVisible ? 'Reglas del juego' : 'Mostrar componente'}</li>
                <li className='menu-links'>Tabla de puntuaci√≥n</li>
                <li className='menu-links' onClick={reloadPage}>Reiniciar juego</li>
            </ul>
        </div>
    );
};

export default Menu;
