import React, { useState } from 'react';
import Modal from './Modal';
import '../styles/Player.scss';

const Player = ({ onPlayersSet }) => {
    const [numPlayers, setNumPlayers] = useState(null);
    const [showModal, setShowModal] = useState(true);

    const handleConfirm = (number) => {
        setNumPlayers(number);
        setShowModal(false);
        const playerNames = Array.from({ length: number }, (_, i) => `Jugador ${i + 1}`);
        onPlayersSet(playerNames); // Pasar los nombres de los jugadores al componente principal
    };

    return (
        <div>
            {showModal && <Modal isOpen={showModal} onClose={() => setShowModal(false)} onConfirm={handleConfirm} />}
            {numPlayers && (
                <div>
                    <h2>Jugadores</h2>
                    <ul>
                        {Array.from({ length: numPlayers }, (_, i) => (
                            <li key={i}>Jugador {i + 1}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Player;
