import React, { useState } from 'react';
import Modal from './Modal';
import '../styles/Player.scss';

const Player = () => {
    const [numPlayers, setNumPlayers] = useState(null);
    const [showModal, setShowModal] = useState(true);

    const handleConfirm = (number) => {
        setNumPlayers(number);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    const playerNames = numPlayers ? Array.from({ length: numPlayers }, (_, i) => `Jugador ${i + 1}`) : [];

    return (
        <div>
            {showModal && <Modal isOpen={showModal} onClose={handleCloseModal} onConfirm={handleConfirm} />}
            <div>
                <h2>Jugadores</h2>
                <ul>
                    {playerNames.map(name => (
                        <li key={name}>{name}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Player;
