import React, { useState } from 'react';
import Dice from './Dice';
import Modal from './Modal';
import '../styles/Game.scss';

const Game = () => {
    const [roll, setRoll] = useState(0);
    const [turnScore, setTurnScore] = useState(0);
    const [rollScore, setRollScore] = useState(0);
    const [diceValues, setDiceValues] = useState([1, 1, 1, 1, 1, 1]);
    const [isModalOpen, setIsModalOpen] = useState(true);
    const [numPlayers, setNumPlayers] = useState(0);
    const [players, setPlayers] = useState([]);
    const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);
    const [showTurnEnd, setShowTurnEnd] = useState(false);
    const [turnScores, setTurnScores] = useState({});

    // Maneja el cierre del modal de ingreso de jugadores
    const handleCloseModal = (players) => {
        setNumPlayers(players.length);
        setPlayers(players.map((name, index) => ({ id: index + 1, name, score: 0 })));
        setIsModalOpen(false);
    };

    // Función para tirar los dados
    const rollDice = () => {
        setRoll(roll + 1);

        const newDiceValues = [...Array(6)].map(() => Math.floor(Math.random() * 6) + 1);
        console.log('Dados de este turno:', newDiceValues);
        setDiceValues(newDiceValues);

        const count = [0, 0, 0, 0, 0, 0];
        newDiceValues.forEach(value => {
            count[value - 1]++;
        });

