import React, { useState } from 'react';
import '../styles/Player.scss';

const Player = () => {
    class Player {
        constructor(name) {
            this.name = name;
            this.score = 0;
            this.isInGame = false; // Nuevo atributo
        }
        // Otros métodos y atributos
    }
}
export default Player;
